    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       try
       {
        boolean erstellen = false;
        Rechnung eineRechnung;
        RechnungsVerwaltung eineVerwaltung = new RechnungsVerwaltung();
        int kundenNr, reservierungsNr, art;
        float rechnungsbetrag;
        
        for(int i=0; i<rechnungAufgabeTBL.getRowCount(); i++)
        {
            erstellen = Boolean.parseBoolean(rechnungAufgabeTBL.getValueAt(i, 0).toString());
            if(erstellen)
            {
                if(rechnungAufgabeTBL.getValueAt(i, 1).toString().equals("Anzahlung"))
                {
                    art=1;
                }
                else
                {
                    art=2;
                }
                kundenNr = Integer.parseInt(rechnungAufgabeTBL.getValueAt(i, 2).toString());
                reservierungsNr = Integer.parseInt(rechnungAufgabeTBL.getValueAt(i, 3).toString());
                rechnungsbetrag = Float.parseFloat(rechnungAufgabeTBL.getValueAt(i, 4).toString());
                eineRechnung = new Rechnung(kundenNr, reservierungsNr, rechnungsbetrag);
                eineRechnung.Speichern(art);
                eineVerwaltung.addRechnung(eineRechnung);
            }
        }
        tblAufbauen();
       }
       catch(Exception e){}
    }//GEN-LAST:event_jButton1ActionPerformed

