    private void tblAufbauen()
    {
        Reservierungsverwaltung eineVerwaltung = new Reservierungsverwaltung();
        LinkedList eineListe, eineListe2 = null;
        ListIterator einIterator, einIterator2;
        int laenge;
        
        eineListe = eineVerwaltung.sucheRechnungsNr1();
        einIterator = eineListe.listIterator();
        
        eineListe2 = eineVerwaltung.sucheRechnungsNr2();
        einIterator2 = eineListe2.listIterator();
        
        laenge = eineListe.size() + eineListe2.size();
        int i=0;
        
        // <editor-fold defaultstate="collapsed" desc=" Tabellen Modell ">
        String[] titel = {"Erstellen", "Art", "Kundennummer", "Reservierungsnummer", "Betrag"};
        boolean[] canEdit = {true, false, false, false, false};
        Class[] types = {Boolean.class, String.class, Integer.class, Integer.class, Float.class};
        
        TabellenModell einModell = new TabellenModell(laenge, titel, canEdit, types);
        rechnungAufgabeTBL.setModel(einModell);
        // </editor-fold>  
        
        while(einIterator.hasNext())
        {
            Reservierung eineReservierung = (Reservierung)einIterator.next();
            //Sonst bekommen wir beim auslesen NullPointerException
            rechnungAufgabeTBL.setValueAt(true, i, 0);
            rechnungAufgabeTBL.setValueAt(false, i, 0);
            rechnungAufgabeTBL.setValueAt("Anzahlung",i,1);
            rechnungAufgabeTBL.setValueAt(eineReservierung.getKundenNR(),i,2);
            rechnungAufgabeTBL.setValueAt(eineReservierung.getResNR(),i,3);
            rechnungAufgabeTBL.setValueAt(eineReservierung.getAnzahlungsbetrag(),i,4);
 
            i++;
        }
        
        while(einIterator2.hasNext())
        {
            Reservierung eineReservierung = (Reservierung)einIterator2.next();
            //Sonst bekommen wir beim auslesen NullPointerException
            rechnungAufgabeTBL.setValueAt(true, i, 0);
            rechnungAufgabeTBL.setValueAt(false, i, 0);
            rechnungAufgabeTBL.setValueAt("Endabrechnung",i,1);
            rechnungAufgabeTBL.setValueAt(eineReservierung.getKundenNR(),i,2);
            rechnungAufgabeTBL.setValueAt(eineReservierung.getResNR(),i,3);
            rechnungAufgabeTBL.setValueAt(eineReservierung.getRechnungsbetrag(),i,4);
 
            i++;
        }
    }
    
